import React, { useState, useCallback } from "react";

import auth0 from "../lib/auth0";

const CreateStatus = () => {
  const [dados, setDados] = useState({
    status: "bem",
    coords: {
      lat: null,
      long: null,
    },
  });

  const getMyLocation = () => {
    console.log("asdasd1");
    console.log("navigator.geolocation: ", navigator.geolocation);
    if (navigator.geolocation) {
      console.log("asdasd");
      navigator.geolocation.getCurrentPosition(function (position) {
        console.log(position);
        setDados((old) => {
          return {
            ...old,
            coords: {
              lat: position.coords.latitude,
              long: position.coords.longitude,
            },
          };
        });
      });
    }
  };

  return (
    <div>
      <h1>CreateStatus</h1>
      <label className="block">
        <input type="radio" name="status" value="bem" />
        Estou bem e sem sintomas
      </label>
      <label className="block">
        <input type="radio" name="status" value="gripe" />
        Estou com sintomas de gripe / resfriado
      </label>
      <label className="block">
        <input type="radio" name="status" value="gripe" />
        Estou com sintomas da COVID-19
      </label>
      Sua posição atual: {JSON.stringify(dados)}
      <button onClick={getMyLocation}>Obter minha localização</button>
    </div>
  );
};

export default CreateStatus;

export async function getServerSideProps({ req, res }) {
  const session = await auth0.getSession(req);

  if (session) {
    return {
      props: {
        isAuth: true,
        user: session.user,
      },
    };
  }
  return {
    props: {
      isAuth: false,
      user: {},
    },
  };
}
